mapper.select:
SELECT ITEM_CD,ITEM_NAME,UNIT_TYPE,PLANT_LINE,BOX_TYPE,BOM_CNT,REG_DT,ITEM_TYPE FROM ( SELECT ROWNUM RN, A.ITEM_CD, B.ITEM_NAME, ( SELECT COM_NAME FROM TB_COM_CODE WHERE COM_GRP_CD = 'IT02' AND B.UNIT_TYPE=COM_CD)AS UNIT_TYPE, ( SELECT COM_NAME FROM TB_COM_CODE WHERE COM_GRP_CD = 'IT04' AND B.PLANT_LINE=COM_CD)AS PLANT_LINE, ( SELECT COM_NAME FROM TB_COM_CODE WHERE COM_GRP_CD = 'IT03' AND B.BOX_TYPE=COM_CD)AS BOX_TYPE, (SELECT COUNT(*)FROM TB_BOM_DTL C WHERE C.ITEM_CD = A.ITEM_CD)AS BOM_CNT, B.REG_DT, B.ITEM_TYPE FROM TB_BOM_INFO A, TB_ITEM_INFO B WHERE B.ITEM_CD = A.ITEM_CD
AND A.ITEM_CD like '%' || #{itemCd} || '%'
AND B.ITEM_NAME = #{itemName}
AND B.ITEM_TYPE = #{itemType} ) WHERE RN > (#{currentPage}-1)*#{countPerPage} AND RN <= #{currentPage}*#{countPerPage}

update.foreach:
INTO TB_BOM_DTL (
	 ITEM_CD,
	 SITEM_CD,
	 BOM_LEVEL,
	 INS_QTY,
	 REG_DT,
	 REG_USER_ID
  )
	VALUES (
 #{itemCd}, #{item.sitemCd}, #{item.bomLevel}, #{item.insQty}
 , SYSDATE, #{userId}
 )

mapper.select:
		SELECT ITEM_CD, ITEM_NAME
        FROM TB_ITEM_INFO 
        WHERE ITEM_CD = #{itemCd}
