mapper.select:
SELECT A.DEPT_CD, A.DEPT_NAME, C.DEPT_NAME AS UP_DEPT_NAME, CASE WHEN NVL(U.USER_COUNT, 0) = 0 THEN '-' ELSE TO_CHAR(NVL(U.USER_COUNT, 0)) END AS USER_COUNT, A.DEL_YN, TO_CHAR(A.REG_DT,'YYYY-MM-DD') REG_DT FROM TB_DEPT_INFO A LEFT JOIN ( SELECT B.DEPT_CD, COUNT(B.DEPT_CD) AS USER_COUNT FROM TB_USER_INFO B WHERE LEAVE_YN = 'N' GROUP BY B.DEPT_CD ) U ON A.DEPT_CD = U.DEPT_CD LEFT JOIN TB_DEPT_INFO C ON A.UP_DEPT_CD = C.DEPT_CD WHERE 1=1
AND A.DEPT_NAME LIKE '%' || #{deptName} || '%'
AND A.DEL_YN = #{delYn}

SELECT A.ITEM_CD, A.ITEM_NAME, B1.COM_NAME AS ITEM_TYPE, B2.COM_NAME AS UNIT_TYPE, B3.COM_NAME AS PLANT_LINE, B4.COM_NAME AS LOCATION, B5.COM_NAME AS BOX_TYPE, TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS REG_DT FROM TB_ITEM_INFO A INNER JOIN TB_COM_CODE B1 ON B1.COM_GRP_CD = 'IT01' AND A.ITEM_TYPE = B1.COM_CD INNER JOIN TB_COM_CODE B2 ON B2.COM_GRP_CD = 'IT02' AND A.UNIT_TYPE = B2.COM_CD INNER JOIN TB_COM_CODE B3 ON B3.COM_GRP_CD = 'IT04' AND A.PLANT_LINE = B3.COM_CD INNER JOIN TB_COM_CODE B4 ON B4.COM_GRP_CD = 'IT05' AND A.LOCATION = B4.COM_CD INNER JOIN TB_COM_CODE B5 ON B5.COM_GRP_CD = 'IT03' AND A.BOX_TYPE = B5.COM_CD WHERE 1=1
AND A.ITEM_CD LIKE '%' || #{itemCd} || '%'
AND A.ITEM_NAME LIKE '%' || #{itemName} || '%'
AND A.ITEM_TYPE = #{itemType}
AND B1.COM_GRP_CD IN
#{comGrpCd}
AND B2.COM_GRP_CD IN
#{comGrpCd}
AND B3.COM_GRP_CD IN
#{comGrpCd}
AND B4.COM_GRP_CD IN
#{comGrpCd}
AND B5.COM_GRP_CD IN
#{comGrpCd}
ORDER BY A.REG_DT DESC

SELECT ROWNUM RN, COMP_CD, COMP_NAME, DECODE(COMP_TYPE, 'S', '공급사', 'C', '고객사', COMP_TYPE) AS COMP_TYPE, BIZ_NO, TRADE_YN, COMP_CEO, PHONE, EMAIL, ADDR, MGR_NAME, MGR_POSITION, MGR_PHONE, DEL_YN, TO_CHAR(REG_DT, 'YYYY-MM-DD') AS REG_DT FROM TB_PARTNER_INFO WHERE 1=1
AND COMP_CD LIKE '%'||#{compCd}||'%'
AND COMP_NAME LIKE '%'||#{compName}||'%'
AND COMP_TYPE LIKE '%'||#{compType}||'%'
AND TO_CHAR(REG_DT,'yyyy-mm-dd') >= #{regDtFrom}
AND TO_CHAR(REG_DT,'yyyy-mm-dd') <= #{regDtTo}

SELECT ROWNUM RN, A.ITEM_CD, B.ITEM_NAME, C.INVOICE_NO, A.IN_QTY, A.IN_PRICE, D.COM_NAME, TO_CHAR(C.IN_DT,'YYYY-MM-DD')IN_DT, C.IN_STATUS, TO_CHAR(A.REG_DT,'YYYY-MM-DD')REG_DT FROM TB_RCV_ITEM A, TB_ITEM_INFO B, TB_RCV_INFO C, TB_COM_CODE D WHERE A.RCV_NO = C.RCV_NO AND A.ITEM_CD = B.ITEM_CD AND A.LOCATION = D.COM_CD AND D.COM_GRP_CD = 'IT05'
AND C.INVOICE_NO LIKE '%' || #{invoiceNo} || '%'
AND C.COMP_CD = #{compCd}
AND TO_CHAR(A.REG_DT,'yyyy-mm-dd') >= #{regDtFrom}
AND TO_CHAR(A.REG_DT,'yyyy-mm-dd') <= #{regDtTo}
AND D.COM_GRP_CD IN
#{comGrpCd}

SELECT A.USER_ID, A.USER_NAME, B.DEPT_NAME, C.COM_NAME, A.LEAVE_YN FROM TB_USER_INFO A INNER JOIN TB_DEPT_INFO B ON A.DEPT_CD = B.DEPT_CD INNER JOIN TB_COM_CODE C ON A.POSITION_CD = C.COM_CD WHERE 1=1
AND USER_NAME LIKE '%'||#{userName}||'%'
AND C.COM_GRP_CD = #{comGrpCd}

SELECT C.PLAN_DT, C.COMP_CD, E.COMP_NAME, C.ITEM_CD, D.ITEM_NAME, C.ORDER_QTY, C.PLAN_QTY, C.PRODUCT_QTY FROM ( SELECT A.PLAN_DT, A.COMP_CD, A.ITEM_CD, B.ORDER_QTY, B.PLAN_QTY, A.PRODUCT_QTY FROM TB_PRODUCT_INFO A, TB_PLAN_INFO B WHERE A.COMP_CD = B.COMP_CD AND A.PLAN_DT = B.PLAN_DT AND A.ITEM_CD = B.ITEM_CD AND TO_CHAR(A.PLAN_DT,'YYYYMM') = #{productDt}
AND A.COMP_CD LIKE '%' || #{compCd} || '%'
) C, TB_ITEM_INFO D, TB_PARTNER_INFO E WHERE C.ITEM_CD = D.ITEM_CD AND C.COMP_CD = E.COMP_CD

WITH PLAN_DATA AS ( SELECT COMP_NAME, ITEM_CD, ITEM_NAME, DECODE(DT, '01', PRODUCT_QTY) AS D1, DECODE(DT, '02', PRODUCT_QTY) AS D2, DECODE(DT, '03', PRODUCT_QTY) AS D3, DECODE(DT, '04', PRODUCT_QTY) AS D4, DECODE(DT, '05', PRODUCT_QTY) AS D5, DECODE(DT, '06', PRODUCT_QTY) AS D6, DECODE(DT, '07', PRODUCT_QTY) AS D7, DECODE(DT, '08', PRODUCT_QTY) AS D8, DECODE(DT, '09', PRODUCT_QTY) AS D9, DECODE(DT, '10', PRODUCT_QTY) AS D10, DECODE(DT, '11', PRODUCT_QTY) AS D11, DECODE(DT, '12', PRODUCT_QTY) AS D12, DECODE(DT, '13', PRODUCT_QTY) AS D13, DECODE(DT, '14', PRODUCT_QTY) AS D14, DECODE(DT, '15', PRODUCT_QTY) AS D15, DECODE(DT, '16', PRODUCT_QTY) AS D16, DECODE(DT, '17', PRODUCT_QTY) AS D17, DECODE(DT, '18', PRODUCT_QTY) AS D18, DECODE(DT, '19', PRODUCT_QTY) AS D19, DECODE(DT, '20', PRODUCT_QTY) AS D20, DECODE(DT, '21', PRODUCT_QTY) AS D21, DECODE(DT, '22', PRODUCT_QTY) AS D22, DECODE(DT, '23', PRODUCT_QTY) AS D23, DECODE(DT, '24', PRODUCT_QTY) AS D24, DECODE(DT, '25', PRODUCT_QTY) AS D25, DECODE(DT, '26', PRODUCT_QTY) AS D26, DECODE(DT, '27', PRODUCT_QTY) AS D27, DECODE(DT, '28', PRODUCT_QTY) AS D28, DECODE(DT, '29', PRODUCT_QTY) AS D29, DECODE(DT, '30', PRODUCT_QTY) AS D30, DECODE(DT, '31', PRODUCT_QTY) AS D31 FROM ( SELECT A.COMP_CD, B.COMP_NAME, A.ITEM_CD, C.ITEM_NAME, TO_CHAR(A.PLAN_DT,'DD') AS DT, A.PRODUCT_QTY FROM TB_PRODUCT_INFO A JOIN TB_PARTNER_INFO B ON A.COMP_CD = B.COMP_CD JOIN TB_ITEM_INFO C ON A.ITEM_CD = C.ITEM_CD WHERE TO_CHAR(A.PLAN_DT,'YYYY') = #{planYear} AND TO_CHAR(A.PLAN_DT,'MM') = #{planMonth}
AND A.COMP_CD = #{compCd}
ORDER BY A.COMP_CD, A.ITEM_CD, DT ) )

SELECT A.ITEM_CD, C.ITEM_NAME, NVL(B.SITEM_CD,' ')SITEM_CD, NVL(D.ITEM_NAME,' ')SITEM_NAME, A.PRODUCT_QTY, NVL(B.INS_QTY,0)INS_QTY , (SELECT A.PRODUCT_QTY * INS_QTY FROM DUAL)AS TOTAL_QTY FROM ( SELECT ITEM_CD, SUM(PRODUCT_QTY) PRODUCT_QTY FROM TB_PRODUCT_INFO WHERE 1=1 AND TO_CHAR(PLAN_DT, 'YYYYMM') = #{planDt}
AND COMP_CD = #{compCd}
GROUP BY ITEM_CD ) A, ( SELECT ITEM_CD, SITEM_CD, INS_QTY FROM TB_BOM_DTL WHERE 1=1 ) B, TB_ITEM_INFO C, TB_ITEM_INFO D WHERE A.ITEM_CD = B.ITEM_CD(+) AND A.ITEM_CD = C.ITEM_CD AND B.SITEM_CD = D.ITEM_CD(+) ORDER BY A.ITEM_CD, B.SITEM_CD
