mapper.select:
SELECT ITEM_CD, ITEM_NAME, LOCATION, UNIT_TYPE, IN_QTY, IN_DT FROM ( SELECT ROWNUM RN, ITEM_CD, ITEM_NAME, LOCATION, UNIT_TYPE, IN_QTY, IN_DT FROM ( SELECT A.ITEM_CD, A.ITEM_NAME, C.LOCATION, B.COM_NAME AS UNIT_TYPE, SUM(C.IN_QTY) AS IN_QTY, TO_CHAR(D.IN_DT, 'YYYYMM') AS IN_DT FROM TB_ITEM_INFO A INNER JOIN TB_RCV_ITEM C ON A.ITEM_CD = C.ITEM_CD INNER JOIN TB_COM_CODE B ON B.COM_GRP_CD = #{comGrpCd} AND A.UNIT_TYPE = B.COM_CD INNER JOIN TB_RCV_INFO D ON C.RCV_NO = D.RCV_NO WHERE 1=1
AND A.ITEM_CD LIKE '%' || #{itemCd} || '%'
AND TO_CHAR(D.IN_DT, 'YYYYMM') = #{inDt} AND A.LOCATION IS NOT NULL GROUP BY A.ITEM_CD, A.ITEM_NAME, C.LOCATION, B.COM_NAME, TO_CHAR(D.IN_DT, 'YYYYMM') ) A ) WHERE RN > (#{currentPage} - 1) * #{countPerPage} AND RN <= #{currentPage} * #{countPerPage}
