mapper.select:
SELECT COUNT(*) FROM TB_ITEM_INFO A INNER JOIN TB_COM_CODE B1 ON B1.COM_GRP_CD = 'IT01' AND A.ITEM_TYPE = B1.COM_CD INNER JOIN TB_COM_CODE B2 ON B2.COM_GRP_CD = 'IT02' AND A.UNIT_TYPE = B2.COM_CD INNER JOIN TB_COM_CODE B3 ON B3.COM_GRP_CD = 'IT04' AND A.PLANT_LINE = B3.COM_CD INNER JOIN TB_COM_CODE B4 ON B4.COM_GRP_CD = 'IT05' AND A.LOCATION = B4.COM_CD INNER JOIN TB_COM_CODE B5 ON B5.COM_GRP_CD = 'IT03' AND A.BOX_TYPE = B5.COM_CD WHERE 1=1
AND A.ITEM_CD LIKE '%' || #{itemCd} || '%'
AND A.ITEM_NAME LIKE '%' || #{itemName} || '%'
AND A.ITEM_TYPE = #{itemType}
AND B1.COM_GRP_CD IN
#{comGrpCd}
AND B2.COM_GRP_CD IN
#{comGrpCd}
AND B3.COM_GRP_CD IN
#{comGrpCd}
AND B4.COM_GRP_CD IN
#{comGrpCd}

SELECT ITEM_CD, ITEM_NAME, WEIGHT, UNIT_PRICE, ITEM_TYPE, UNIT_TYPE, PLANT_LINE, LOCATION, BOX_TYPE, BOX_QTY, REG_DT FROM( SELECT ROWNUM AS RN, A.ITEM_CD, A.ITEM_NAME, A.WEIGHT, A.UNIT_PRICE, B1.COM_NAME AS ITEM_TYPE, B2.COM_NAME AS UNIT_TYPE, B3.COM_NAME AS PLANT_LINE, B4.COM_NAME AS LOCATION, B5.COM_NAME AS BOX_TYPE, A.BOX_QTY, TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS REG_DT FROM TB_ITEM_INFO A INNER JOIN TB_COM_CODE B1 ON B1.COM_GRP_CD = 'IT01' AND A.ITEM_TYPE = B1.COM_CD INNER JOIN TB_COM_CODE B2 ON B2.COM_GRP_CD = 'IT02' AND A.UNIT_TYPE = B2.COM_CD INNER JOIN TB_COM_CODE B3 ON B3.COM_GRP_CD = 'IT04' AND A.PLANT_LINE = B3.COM_CD INNER JOIN TB_COM_CODE B4 ON B4.COM_GRP_CD = 'IT05' AND A.LOCATION = B4.COM_CD INNER JOIN TB_COM_CODE B5 ON B5.COM_GRP_CD = 'IT03' AND A.BOX_TYPE = B5.COM_CD WHERE 1=1
AND A.ITEM_CD LIKE '%' || #{itemCd} || '%'
AND A.ITEM_NAME LIKE '%' || #{itemName} || '%'
AND A.ITEM_TYPE = #{itemType}
AND B1.COM_GRP_CD IN
#{comGrpCd}
AND B2.COM_GRP_CD IN
#{comGrpCd}
AND B3.COM_GRP_CD IN
#{comGrpCd}
AND B4.COM_GRP_CD IN
#{comGrpCd}
AND B5.COM_GRP_CD IN
#{comGrpCd}
ORDER BY A.REG_DT DESC ) WHERE RN > (#{currentPage}-1)*#{countPerPage} AND RN <= #{currentPage}*#{countPerPage}
