mapper.delete:
	DELETE FROM TB_PLAN_INFO
        WHERE TO_CHAR(PLAN_DT,'YYYYMM') = #{planYear}||#{planMonth}
         AND COMP_CD = #{compCd}

mapper.select:
SELECT COMP_CD, COMP_NAME, ITEM_CD, ITEM_NAME, PLAN_DT, PLAN_QTY FROM ( SELECT A.COMP_CD, B.COMP_NAME, A.ITEM_CD, C.ITEM_NAME, TO_CHAR(A.PLAN_DT,'DD')PLAN_DT, A.PLAN_QTY FROM TB_PLAN_INFO A, TB_PARTNER_INFO B, TB_ITEM_INFO C WHERE A.COMP_CD = B.COMP_CD AND A.ITEM_CD = C.ITEM_CD AND TO_CHAR(A.PLAN_DT,'YYYY') = #{mkPlanYear} AND TO_CHAR(A.PLAN_DT,'MM') = #{mkPlanMonth}
AND A.COMP_CD = #{compCd}
ORDER BY A.PLAN_DT )

SELECT ITEM_CD, ITEM_NAME, TOTAL_PLAN_QTY, RN FROM ( SELECT ROWNUM RN, ITEM_CD, ITEM_NAME, TOTAL_PLAN_QTY FROM ( SELECT A.ITEM_CD, B.ITEM_NAME, SUM(A.PLAN_QTY) TOTAL_PLAN_QTY FROM TB_PLAN_INFO A, TB_ITEM_INFO B WHERE A.ITEM_CD = B.ITEM_CD AND A.PLAN_QTY > 0 AND TO_CHAR(A.PLAN_DT, 'yyyy-mm-dd') > #{palnDtFrom} AND TO_CHAR(A.PLAN_DT, 'yyyy-mm-dd') <= #{palnDtTo}
AND A.ITEM_CD LIKE '%'||#{itemCd}||'%'
AND A.COMP_CD LIKE '%'||#{compCd}||'%'
GROUP BY A.ITEM_CD, B.ITEM_NAME ) ) WHERE RN > (#{currentPage}-1)*#{countPerPage} AND RN <= #{currentPage}*#{countPerPage}

SELECT COUNT(*) FROM( SELECT A.ITEM_CD, B.ITEM_NAME, A.INS_QTY, B.BOX_QTY FROM TB_BOM_DTL A, TB_ITEM_INFO B WHERE A.ITEM_CD = B.ITEM_CD AND A.ITEM_CD IN ( SELECT SITEM_CD FROM TB_BOM_DTL WHERE ITEM_CD IN
#{item.itemCd}
) )

SELECT RN, SITEM_CD, DEMAND_QTY, ITEM_NAME, BOX_QTY FROM ( SELECT ROWNUM AS RN, SITEM_CD, DEMAND_QTY, ITEM_NAME, BOX_QTY FROM ( SELECT A.SITEM_CD, SUM(A.INS_QTY * B. ORDER_QTY ) DEMAND_QTY FROM TB_BOM_DTL A, TB_PLAN_INFO B WHERE A.ITEM_CD = B.ITEM_CD AND A.ITEM_CD IN
#{item.itemCd}
GROUP BY A.SITEM_CD ) A, TB_ITEM_INFO B WHERE A.SITEM_CD = B.ITEM_CD ) WHERE RN > (#{currentPage}-1)*#{countPerPage} AND RN <= #{currentPage}*#{countPerPage}
