mapper.select:
SELECT A.ITEM_CD, B.ITEM_NAME,C.COMP_NAME FROM TB_PARTNER_ITEM A, TB_ITEM_INFO B ,TB_PARTNER_INFO C WHERE A.ITEM_CD = B.ITEM_CD AND A.COMP_CD = C.COMP_CD
AND A.COMP_CD = #{compCd}
ORDER BY A.ITEM_CD

mapper.delete:
	DELETE FROM TB_PRODUCT_INFO
    WHERE COMP_CD = #{compCd}
    AND PLAN_DT = #{planDt}

update.foreach:
INTO TB_PRODUCT_INFO (
	 COMP_CD,
	 PLAN_DT,
	 ITEM_CD,
	 USER_ID,
	 PRODUCT_QTY,
	 REG_DT,
	 REG_USER_ID
  )
	VALUES (
 #{compCd},#{planDt}, #{item.itemCd}, #{item.userId}
 ,  #{item.productQty}, SYSDATE, #{regUserId}
 )

mapper.select:
SELECT A.ITEM_CD, C.ITEM_NAME, NVL(B.SITEM_CD,' ')SITEM_CD, NVL(D.ITEM_NAME,' ')SITEM_NAME, A.PRODUCT_QTY, NVL(B.INS_QTY,0)INS_QTY FROM ( SELECT ITEM_CD, SUM(PRODUCT_QTY) PRODUCT_QTY FROM TB_PRODUCT_INFO WHERE 1=1 AND TO_CHAR(PLAN_DT, 'YYYYMM') = #{planDt}
AND COMP_CD = #{compCd}
GROUP BY ITEM_CD ) A, ( SELECT ITEM_CD, SITEM_CD, INS_QTY FROM TB_BOM_DTL WHERE 1=1 ) B, TB_ITEM_INFO C, TB_ITEM_INFO D WHERE A.ITEM_CD = B.ITEM_CD(+) AND A.ITEM_CD = C.ITEM_CD AND B.SITEM_CD = D.ITEM_CD(+) ORDER BY A.ITEM_CD, B.SITEM_CD

SELECT COUNT(*) FROM ( SELECT A.PLAN_DT, A.COMP_CD, A.ITEM_CD, B.ORDER_QTY, B.PLAN_QTY, A.PRODUCT_QTY FROM TB_PRODUCT_INFO A, TB_PLAN_INFO B WHERE A.COMP_CD = B.COMP_CD AND A.PLAN_DT = B. PLAN_DT AND A.ITEM_CD = B. ITEM_CD
AND A.COMP_CD LIKE '%' || #{compCd} || '%'
)C, TB_ITEM_INFO D, TB_PARTNER_INFO E WHERE C.ITEM_CD = D.ITEM_CD AND C.COMP_CD = E.COMP_CD AND TO_CHAR(C.PLAN_DT,'YYYYMM') = #{productDt}

SELECT RN, C.PLAN_DT, C.COMP_CD, E.COMP_NAME, C.ITEM_CD, D.ITEM_NAME, C.ORDER_QTY, C.PLAN_QTY, C.PRODUCT_QTY FROM ( SELECT ROW_NUMBER() OVER (ORDER BY A.COMP_CD, A.ITEM_CD) AS RN, A.PLAN_DT, A.COMP_CD, A.ITEM_CD, B.ORDER_QTY, B.PLAN_QTY, A.PRODUCT_QTY FROM TB_PRODUCT_INFO A, TB_PLAN_INFO B WHERE A.COMP_CD = B.COMP_CD AND A.PLAN_DT = B.PLAN_DT AND A.ITEM_CD = B.ITEM_CD AND TO_CHAR(A.PLAN_DT,'YYYYMM') = #{productDt}
AND A.COMP_CD LIKE '%' || #{compCd} || '%'
) C, TB_ITEM_INFO D, TB_PARTNER_INFO E WHERE C.ITEM_CD = D.ITEM_CD AND C.COMP_CD = E.COMP_CD AND RN > (#{currentPage} - 1) * #{countPerPage} AND RN <= #{currentPage} * #{countPerPage}

SELECT COUNT(*)
FROM (SELECT A.COMP_CD, A.ITEM_CD, B.SITEM_CD FROM TB_PRODUCT_INFO A, TB_BOM_DTL B WHERE A.ITEM_CD = B.ITEM_CD AND TO_CHAR(A.PLAN_DT,'YYYY') = #{mthProYear} AND TO_CHAR(A.PLAN_DT,'MM') = #{mthProMonth}
AND A.COMP_CD = #{compCd}
)

SELECT COMP_CD, ITEM_CD, ITEM_NAME, SITEM_CD, SITEM_NAME, PRODUCT_QTY, INS_QTY, DEMAND_QTY, PLAN_DT FROM (SELECT ROWNUM RN, A.COMP_CD, A.ITEM_CD, C.ITEM_NAME, B.SITEM_CD, D.ITEM_NAME AS SITEM_NAME, A.PRODUCT_QTY, B.INS_QTY, A.PRODUCT_QTY*B.INS_QTY AS DEMAND_QTY, A.PLAN_DT FROM TB_PRODUCT_INFO A, TB_BOM_DTL B, TB_ITEM_INFO C, TB_ITEM_INFO D WHERE A.ITEM_CD = B.ITEM_CD AND A.ITEM_CD = C.ITEM_CD AND B.SITEM_CD = D.ITEM_CD AND TO_CHAR(A.PLAN_DT,'YYYY') = #{mthProYear} AND TO_CHAR(A.PLAN_DT,'MM') = #{mthProMonth}
AND A.COMP_CD = #{compCd}
) WHERE RN > (#{currentPage} - 1) * #{countPerPage} AND RN <= #{currentPage} * #{countPerPage}
