mapper.select:
SELECT COUNT(*) FROM TB_RCV_INFO A INNER JOIN TB_RCV_ITEM B ON A.RCV_NO = B.RCV_NO INNER JOIN TB_ITEM_INFO C ON B.ITEM_CD = C.ITEM_CD WHERE 1=1
AND A.INVOICE_NO '%' || #{invoiceNo} || '%'
AND A.COMP_CD = #{compCd}
AND TO_CHAR(A.REG_DT,'yyyy-mm-dd') >= #{regDtFrom}
AND TO_CHAR(A.REG_DT,'yyyy-mm-dd') <= #{regDtTo}

SELECT ITEM_CD, ITEM_NAME, INVOICE_NO, IN_QTY, IN_PRICE, COM_NAME, IN_DT, IN_STATUS, REG_DT FROM( SELECT ROWNUM RN, A.ITEM_CD, B.ITEM_NAME, C.INVOICE_NO, A.IN_QTY, A.IN_PRICE, D.COM_NAME, TO_CHAR(C.IN_DT,'YYYY-MM-DD')IN_DT, C.IN_STATUS, TO_CHAR(A.REG_DT,'YYYY-MM-DD')REG_DT FROM TB_RCV_ITEM A, TB_ITEM_INFO B, TB_RCV_INFO C, TB_COM_CODE D WHERE A.RCV_NO = C.RCV_NO AND A.ITEM_CD = B.ITEM_CD AND A.LOCATION = D.COM_CD AND D.COM_GRP_CD = #{comGrpCd}
AND C.INVOICE_NO LIKE '%' || #{invoiceNo} || '%'
AND C.COMP_CD = #{compCd}
AND TO_CHAR(A.REG_DT,'yyyy-mm-dd') >= #{regDtFrom}
AND TO_CHAR(A.REG_DT,'yyyy-mm-dd') <= #{regDtTo}
) WHERE RN > (#{currentPage}-1)*#{countPerPage} AND RN <= #{currentPage}*#{countPerPage}

SELECT IN_DT, COMP_NAME, COMP_CD, RCV_NO, INVOICE_NO, TOTAL_PRICE, ITEM_CNT, REG_DT, RN FROM ( SELECT TO_CHAR(A.IN_DT, 'YYYY-MM-DD') AS IN_DT, C.COMP_NAME, A.COMP_CD, A.INVOICE_NO, A.RCV_NO, B.TOTAL_PRICE, B.ITEM_CNT, TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS REG_DT, ROW_NUMBER() OVER(ORDER BY A.REG_DT DESC) RN FROM TB_RCV_INFO A, (SELECT RCV_NO, COUNT(*) ITEM_CNT, SUM(IN_QTY * IN_PRICE) TOTAL_PRICE FROM TB_RCV_ITEM GROUP BY RCV_NO) B, TB_PARTNER_INFO C WHERE A.RCV_NO = B.RCV_NO AND A.COMP_CD = C.COMP_CD
AND A.INVOICE_NO LIKE '%' || #{invoiceNo} || '%'
AND C.COMP_NAME LIKE '%' || #{compName} || '%'
AND TO_CHAR(IN_DT,'yyyy-mm-dd') >= #{inDtFrom}
AND TO_CHAR(IN_DT,'yyyy-mm-dd') >= #{inDtTo}
) WHERE RN > (#{currentPage}-1)*#{countPerPage} AND RN <= #{currentPage}*#{countPerPage}

update.foreach:
INTO TB_RCV_ITEM (
	 RCV_NO,
	 ITEM_CD,
	 IN_QTY,
	 IN_PRICE,
	 LOCATION,
	 REG_DT,
	 REG_USER_ID
  )
	VALUES (
 #{rcvNo}, #{item.itemCd}, #{item.inQty}, #{item.inPrice}
 , #{item.location},SYSDATE,#{userId}
 )
